import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import Memberroot from './Memberroot';

const Members = ({ baseurl }) => {

    const [allmembersValues, setallmembersValues] = useState([]);

    const fetchallmember = `/user`;

    const addItem = (newItem) => {
        setallmembersValues(prevList => [...prevList, newItem]);
    };

    useEffect(() => {
        async function fetchAllMembers() {
            axios.get(baseurl + fetchallmember).then((res) => {
                for (let i = 0; i < res.data.length; i++) {
                    addItem(res.data[i]);
                }
                setallmembersValues(allmembersValues);
                console.log("type: " + typeof (allmembersValues));
                console.log("data: " + allmembersValues);
            });
        };
        fetchAllMembers();
    }, []);



    return (
        <>
            <div className="row">
                <Memberroot allmembers={allmembersValues} />
            </div>
        </>
    );
}

export default Members;
