import React, { ReactNode } from "react";
import { motion } from "framer-motion";
import "./BlackBoxTransition.css";

 const BlackBoxTransition = ({ children }: {children: ReactNode}) => {
    const blackBox = {
        initial: {
            height: "100vh",
            bottom: 0,
        },
        animate: {
            height: 0,
            opacity: 1,
            transition: {
                duration: 1,
                ease: [0.87, 0, 0.13, 1],
            }
        },
    };
    return (
        <div className="absolute">
            <motion.div initial="initial" animate="animate" variants={blackBox} className="relative bg-black w-full">{children}</motion.div>
        </div>
    );
 };
export default BlackBoxTransition;
